package Kernel::Output::HTML::NavBarABC;

use strict;
use warnings;

# --
sub new {
    my ( $Type, %Param ) = @_;
    [...]
    return $Self;
}
# --
sub Run {
    my ( $Self, %Param ) = @_;
    my %Return = ();
    $Return{'0999989'} = {
        Block       => 'ItemPersonal',
        Description => 'Some Desctipton',
        Name        => 'Text',
        Image       => 'new-message.png',
        Link        => 'Action=AgentMailbox&amp;Subaction=New',
        AccessKey   => 'j',
    };
    return %Return;
}
